config.files:
{
    "server.properties": {
        "parser": "properties",
        "find": {
            "server-ip": "0.0.0.0",
            "server-port": "{{server.allocations.default.port}}"
        }
    },
    ".\/autoplug\/general.yml": {
        "parser": "file",
        "find": {
            "    key:": "    key: {{server.environment.SERVER_KEY}}",
            "    start-command: ": "    start-command: java -Xms{{server.environment.SERVER_RAM}}M -Xmx{{server.environment.SERVER_RAM}}M {{server.environment.JAVA_ARGS}} -jar \".\/{{server.environment.SERVER_TYPE}}.jar\""
        }
    },
    ".\/autoplug\/updater.yml": {
        "parser": "file",
        "find": {
            "    software:": "    software: {{server.environment.SERVER_TYPE}}",
            "    version: ": "    version: {{server.environment.GAME_VERSION}}"
        }
    }
}
