config.files:
{
    "server.properties": {
        "parser": "properties",
        "find": {
            "server-ip": "0.0.0.0",
            "server-port": "{{server.build.default.port}}"
        }
    },
    ".\/autoplug\/general.yml": {
        "parser": "file",
        "find": {
            "    key:": "    key: {{server.environment.SERVER_KEY}}",
            "    start-command: ": "    start-command: java -Xms{{server.build.memory|math:"x - 1024"}}M -Xmx{{server.build.memory|math:"x - 1024"}}M {{server.environment.JAVA_ARGS}} -jar \".\/{{server.environment.SERVER_TYPE}}.jar\""
        }
    },
    ".\/autoplug\/updater.yml": {
        "parser": "file",
        "find": {
            "    software:": "    software: {{server.environment.SERVER_TYPE}}",
            "    version: ": "    version: {{server.environment.GAME_VERSION}}"
        }
    }
}
